
var error = function( err, datas ){    
    if( err && err.code && err.message ){
        this.name = err.code;
        this.message = err.message;
    }
    if( datas )
        this.datas = datas;
    Error.captureStackTrace(this,error);
};

error.prototype = new Error();
error.prototype.constructor = error;

module.exports = error;