
var error = function( name, code, message, datas ){
    if( name )
        this.name = name;
    if( code )
        this.code = code;
    if( message )
        this.message = message;
    if( datas )
        this.datas = datas;
    Error.captureStackTrace(this,error);
};

error.prototype = new Error();
error.prototype.constructor = error;

module.exports = error;