/**
 * Utility to execute todo functions sequencially
 */

module.exports = function( fns ){
    var l=fns.length-1,
        args = [],
        f = undefined;
    
    for(; l>=0; l-- ){
        f = (function(i,fn){            
            return function(){ fns[i].apply(null,args.concat(fn)); };            
        })(l,f, args);
    }
    
    return function(){
        args = Array.prototype.slice.call(arguments,0);
        f();
    };
};