



var adapter = require('./memory/adapater.js');

var helper = function( context ){
    
    this.ctx = context;
    
    this.cookies_helper = this.app.helperManager.get('cookies',this.ctx);
    
    this.store_adapter = this.app.options['session']['adapter']?require(this.app.options['session']['adapter']):adapter;
    
    
    
    
    
};




module.exports = helper;