/**
 * Utility to execute todo functions sequencially
 * @param {type} todo
 * @param {type} callback
 * @returns {undefined}
 */


module.exports = function( fns ){
    var l=fns.length-1,
        args = [],
        f = undefined;
    
    for(; l>=0; l-- ){
        f = (function(i,fn){            
            return function(){ fns[i].apply(null,args.concat(fn)); };            
        })(l,f, args);
    }
    
    return function(){
        args.concat( Array.prototype.slice.apply(arguments,0) );
        f();
    };
};