/**
 * Extend first parameter object with all properties from other object parameter 
 * Params: objectA, objectB, ...
 * @returns object
 */
module.exports = function(){
    var r = arguments[0]||{}, l = arguments.length, i=1;    
    if( l>1 ){
        for(;i<l;i++){
            for( var k in arguments[i] )
                r[k] = arguments[i][k];
        }
    }
    return r;
};