/* 
 * JHTML Template object
 */

var jtpl = function(application,datas){
    this.app = application;
    this.datas = datas;
    this.childs = {};
};

jtpl.prototype.addChild =function(name, child){
    if( child instanceof jtpl )
        this.childs[name] = child;
};

jtpl.prototype.child = function(name,datas){
    return this.childs[name]?this.childs[name].render(datas):'';
};

// Always overrided by jhtml template builder.
jtpl.prototype.render = function(){return '';};

module.exports = jtpl;